import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Increment, Decrement } from '../redux/counterAction';

export default function CounterRedux() {
  const dispatch = useDispatch();

  const counter = useSelector((state) => state.counter);

  return (
    <div className="h-screen grid md:grid-cols-2 grid-rows-2">
      <div className="bg-[#C49BF8] md:h-screen h-full">
        <h1 className="absolute left-10 top-6 md:text-4xl text-xl text-white font-semibold font-mono">
          Question 1
        </h1>
      </div>
      <div className="md:h-screen h-full bg-[#7E91C0] space-y-6 flex flex-col justify-center items-center">
        <span>{counter}</span>
        <div className="flex flex-col space-y-4">
          <button onClick={() => dispatch(Increment())} className="border">
            Increment
          </button>
          <button onClick={() => dispatch(Decrement())} className="border">
            Decrement
          </button>
        </div>
      </div>
    </div>
  );
}
