import { useState } from 'react';

export default function FunctionExample() {
  const [message, setMessage] = useState('');

  const handleChange = (event) => {
    setMessage(event.target.value);

    console.log('value is:', event.target.value);
  };

  return (
    <div className="h-screen bg-[#96BEA4]">
      <div>
        <div className="text-2xl font-semibold font-mono text-center space-y-4">
          <h1 className="text-white">This is from Class Component.</h1>
          <p className="text-[#800080]">
            Use of Class State in Class Component
          </p>
        </div>
      </div>
      <input
        type="text"
        id="message"
        name="message"
        onChange={handleChange}
        value={message}
        className=""
      />

      <h2>{message}</h2>
    </div>
  );
}
