import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Increment, Decrement } from '../redux/counterAction';

export default function CounterRedux() {
  const dispatch = useDispatch();

  const counter = useSelector((state) => state.counter);

  return (
    <div className="h-screen grid md:grid-cols-2 grid-rows-2">
      <div></div>
      <div>
        <span>{counter}</span>
        <div className="flex flex-col space-y-4">
          <button onClick={() => dispatch(Increment())} className="border">
            Increment
          </button>
          <button onClick={() => dispatch(Decrement())} className="border">
            Decrement
          </button>
        </div>
      </div>
    </div>
  );
}
