import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

export default function FunctionExample() {
  const dispatch = useDispatch();

  const counter = useSelector((state) => state.counter);

  const [message, setMessage] = useState('');

  const handleChange = (event) => {
    setMessage(event.target.value);

    console.log('value is:', event.target.value);
  };

  return (
    <div className="h-screen bg-[#96BEA4]">
      <div className="md:h-full h-[50%] space-y-6 flex flex-col justify-center items-center">
        <div className="text-2xl font-semibold font-mono text-center space-y-4">
          <h1 className="text-white">This is from Functional Component.</h1>
          <p className="text-[#FF0000]">Use of Hooks in Functional Component</p>
        </div>
        <input
          type="text"
          id="message"
          name="message"
          onChange={handleChange}
          value={message}
          className="rounded-sm border-2 border-black p-2 outline-none"
        />
        <div className="text-2l font-semibold font-mono text-center text-white space-y-4">
          <h2>{message}</h2>
        </div>
      </div>
    </div>
  );
}
