import React, { Component } from 'react';

class classExample extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputValue: '',
    };
  }

  handleChange = (event) => {
    this.setState({ inputValue: event.target.value });
  };

  render() {
    return (
      <div className="bg-[#ED806E] h-screen">
        <h1 className="text-xl font-semibold font-mono">Question 1</h1>
        <div className="content-center flex flex-col items-center justify-center space-y-6">
          <div className="text-xl font-semibold font-mono text-center space-y-4">
            <h1 className="text-white">This is from Class Component.</h1>
            <p className="text-[#800080]">
              Use of Class State in Class Component
            </p>
          </div>
          <input
            type="text"
            value={this.state.inputValue}
            placeholder="Enter text here"
            onChange={this.handleChange}
            className="rounded-sm border-2 border-black p-2 outline-none"
          />
          <div className="text-xl font-semibold font-mono text-center space-y-4">
            {this.state.inputValue}
          </div>
        </div>
      </div>
    );
  }
}

export default classExample;
